from flask_sqlalchemy import SQLAlchemyfrom datetime import datetimedb = SQLAlchemy()class Workout(db.Model): # <- Sagt: "Erstelle eine Workout-Tabelle"    id = db.Column(db.Integer, primary_key=True)    name = db.Column(db.String(100), nullable=False) ## <- Wie eine Excel-Spalte f체r Text    description = db.Column(db.Text) # <- eine Spalte f체r Beschreibung    duration = db.Column(db.Integer) ## <- Eine Spalte f체r Zahlen    difficulty = db.Column(db.Enum('Anf채nger', 'Fortgeschritten', 'Profi'))    created_by = db.Column(db.Integer, db.ForeignKey('user.id'))  # Falls User-Tabelle existiert    created_at = db.Column(db.DateTime, default=datetime.utcnow)    def __repr__(self):        return f'<Workout {self.name}>'